DROP TABLE IF EXISTS `news`;
CREATE TABLE `news`  (
  `nid` int(11) NOT NULL AUTO_INCREMENT,
  `ntid` int(11) NOT NULL,
  `ntitle` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `nauthor` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `ncreatedate` datetime(0) NOT NULL,
  `npicpath` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `ncontent` varchar(4000) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `nmodifydate` datetime(0) NULL DEFAULT NULL,
  `nsummary` varchar(2000) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  PRIMARY KEY (`nid`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 207 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

