-- ----------------------------
-- Triggers structure for table topic
-- ----------------------------
DROP TRIGGER IF EXISTS `topicDelet`;
delimiter ;;
CREATE TRIGGER `topicDelet` BEFORE DELETE ON `topic` FOR EACH ROW begin

delete from comments using comments,news  WHERE news.nid=comments.cnid  and news.ntid=old.tid;
delete from news where news.ntid=old.tid;

end
;;
delimiter ;

